import React, { useState, useContext, useEffect, useCallback } from 'react';
import { AuthContext } from '../AuthContext';
import UpdatePasswordModal from '../components/UpdatePasswordModal';

/**
 * StoreOwnerDashboard Component:
 * Displays dashboard functionalities for store owners,
 * including average store rating and a list of users who submitted ratings.
 */
function StoreOwnerDashboard() {
  // --- React Hooks must be called unconditionally at the top level ---
  const authContext = useContext(AuthContext);
  const [storeRatings, setStoreRatings] = useState([]);
  const [usersWhoRatedStores, setUsersWhoRatedStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showUpdatePasswordModal, setShowUpdatePasswordModal] = useState(false); // <--- ADDED: State for password modal

  // Memoize fetchStoreOwnerRatings using useCallback
  const fetchStoreOwnerRatings = useCallback(async () => {
    setLoading(true);
    setError('');
    // Ensure token is available before making API call
    if (!authContext || !authContext.token) {
      setError('Authentication token not found. Please log in.');
      setLoading(false);
      return;
    }

    try {
      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${authContext.token}`,
      };

      // Fetch both average store ratings and users who rated
      const [storeRatingsRes, usersRatedRes] = await Promise.all([
        fetch('http://localhost:5000/api/ratings/owner', { headers }),
        fetch('http://localhost:5000/api/ratings/owner/users-rated', { headers }),
      ]);

      if (!storeRatingsRes.ok || !usersRatedRes.ok) {
        const storeError = !storeRatingsRes.ok ? `Store ratings fetch failed: ${storeRatingsRes.statusText}` : '';
        const usersError = !usersRatedRes.ok ? `Users rated fetch failed: ${usersRatedRes.statusText}` : '';
        throw new Error(`Failed to fetch dashboard data. ${storeError} ${usersError}`);
      }

      const storeRatingsData = await storeRatingsRes.json();
      const usersRatedData = await usersRatedRes.json();

      setStoreRatings(storeRatingsData);
      setUsersWhoRatedStores(usersRatedData);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [authContext]);

  useEffect(() => {
    if (authContext && authContext.token) {
      fetchStoreOwnerRatings();
    } else {
      setLoading(false);
      setError('Not authenticated. Please log in.');
    }
  }, [authContext, fetchStoreOwnerRatings]);
  // --- End of React Hooks section ---


  // Defensive check for authContext after all hooks are called
  if (!authContext) {
    console.error("AuthContext is null in StoreOwnerDashboard. Ensure it's rendered within AuthProvider.");
    return (
      <div className="flex justify-center items-center h-screen text-2xl text-red-600 flex-col">
        Authentication context not available. Please log in again.
      </div>
    );
  }

  const { token, logout } = authContext;


  if (loading) {
    return <div className="flex justify-center items-center h-screen text-2xl text-gray-700">Loading dashboard...</div>;
  }

  if (error) {
    return <div className="flex justify-center items-center h-screen text-2xl text-red-600">{error}</div>;
  }

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <header className="flex justify-between items-center mb-10 pb-4 border-b border-gray-200">
        <h1 className="text-4xl font-extrabold text-gray-900">Store Owner Dashboard</h1>
        <div className="flex items-center space-x-4">
          <button
            onClick={() => setShowUpdatePasswordModal(true)}
            className="py-2 px-6 bg-yellow-600 text-white font-semibold rounded-lg shadow-md hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-yellow-500 focus:ring-offset-2 transition duration-300"
          >
            Update Password
          </button>
          <button
            onClick={logout}
            className="py-2 px-6 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 transition duration-300"
          >
            Logout
          </button>
        </div>
      </header>

      <section className="mb-12">
        <h2 className="text-3xl font-bold text-gray-900 mb-6">Your Stores' Ratings Overview</h2>
        {storeRatings.length === 0 ? (
          <div className="bg-white p-6 rounded-xl shadow-lg text-center text-gray-600">
            No stores found or no ratings submitted for your stores yet.
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {storeRatings.map((store) => (
              <div key={store.name} className="bg-white p-6 rounded-xl shadow-lg flex flex-col items-center justify-center transform transition-transform hover:scale-105">
                <h3 className="text-2xl font-semibold text-gray-800 mb-2">{store.name}</h3>
                <p className="text-xl text-gray-600 mb-4">Average Rating:</p>
                <p className="text-5xl font-bold text-blue-600">
                  {/* Robust check for avg_rating: ensure it's a valid number or can be parsed to one */}
                  {typeof store.avg_rating === 'number' && !isNaN(store.avg_rating)
                    ? store.avg_rating.toFixed(2)
                    : (typeof store.avg_rating === 'string' && !isNaN(parseFloat(store.avg_rating))
                        ? parseFloat(store.avg_rating).toFixed(2)
                        : 'N/A')}
                </p>
                <p className="text-md text-gray-500 mt-2">({store.total_ratings} total ratings)</p>
              </div>
            ))}
          </div>
        )}
      </section>

      <section>
        <h2 className="text-3xl font-bold text-gray-900 mb-6">Users Who Rated Your Stores</h2>
        {usersWhoRatedStores.length === 0 ? (
          <div className="bg-white p-6 rounded-xl shadow-lg text-center text-gray-600">
            No users have submitted ratings for your stores yet.
          </div>
        ) : (
          <div className="bg-white rounded-xl shadow-lg p-6 overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User Name</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User Email</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Store Rated</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Rating</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {usersWhoRatedStores.map((ratingEntry, index) => (
                  <tr key={index} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{ratingEntry.user_name}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{ratingEntry.user_email}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{ratingEntry.store_name}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{ratingEntry.rating}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </section>

      {showUpdatePasswordModal && (
        <UpdatePasswordModal
          onClose={() => setShowUpdatePasswordModal(false)}
        />
      )}
    </div>
  );
}

export default StoreOwnerDashboard;
