import React from 'react';
import { Table, Form, Button, Card } from 'react-bootstrap'; // Import Bootstrap components

/**
 * UserTable Component:
 * Displays a sortable and filterable table of users.
 * @param {Array} users - Array of user objects to display.
 * @param {Object} filters - Current filter values for each column.
 * @param {Function} onFilterChange - Callback function for filter input changes.
 * @param {Function} onSort - Callback function for sorting column clicks.
 * @param {Object} sortConfig - Current sort configuration (key and direction).
 */
function UserTable({ users, filters, onFilterChange, onSort, sortConfig }) {
  // Helper function to display sort indicator (arrow)
  const getSortIndicator = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? ' ▲' : ' ▼';
    }
    return '';
  };

  return (
    <Card className="shadow-sm p-3"> {/* Replaced bg-white rounded-xl shadow-lg p-6 overflow-x-auto */}
      <Table striped bordered hover responsive className="mb-0"> {/* Replaced min-w-full divide-y divide-gray-200 */}
        <thead className="bg-light"> {/* Replaced bg-gray-50 */}
          <tr>
            {/* Table Headers with Sort Buttons and Filter Inputs */}
            <th className="align-middle text-start text-muted text-uppercase small fw-medium"> {/* Combined multiple Tailwind classes into Bootstrap equivalents */}
              <div className="d-flex align-items-center"> {/* Replaced flex items-center */}
                Name
                <Button variant="link" className="p-0 ms-1 text-decoration-none text-dark" onClick={() => onSort('name')}> {/* Replaced ml-1 focus:outline-none */}
                  {getSortIndicator('name')}
                </Button>
              </div>
              <Form.Control
                type="text"
                name="name"
                value={filters.name}
                onChange={onFilterChange}
                placeholder="Filter Name"
                className="mt-1 form-control-sm" // Replaced mt-1 block w-full px-2 py-1 border border-gray-300 rounded-md text-sm
              />
            </th>
            <th className="align-middle text-start text-muted text-uppercase small fw-medium">
              <div className="d-flex align-items-center">
                Email
                <Button variant="link" className="p-0 ms-1 text-decoration-none text-dark" onClick={() => onSort('email')}>
                  {getSortIndicator('email')}
                </Button>
              </div>
              <Form.Control
                type="text"
                name="email"
                value={filters.email}
                onChange={onFilterChange}
                placeholder="Filter Email"
                className="mt-1 form-control-sm"
              />
            </th>
            <th className="align-middle text-start text-muted text-uppercase small fw-medium">
              <div className="d-flex align-items-center">
                Address
                <Button variant="link" className="p-0 ms-1 text-decoration-none text-dark" onClick={() => onSort('address')}>
                  {getSortIndicator('address')}
                </Button>
              </div>
              <Form.Control
                type="text"
                name="address"
                value={filters.address}
                onChange={onFilterChange}
                placeholder="Filter Address"
                className="mt-1 form-control-sm"
              />
            </th>
            <th className="align-middle text-start text-muted text-uppercase small fw-medium">
              <div className="d-flex align-items-center">
                Role
                <Button variant="link" className="p-0 ms-1 text-decoration-none text-dark" onClick={() => onSort('role')}>
                  {getSortIndicator('role')}
                </Button>
              </div>
              <Form.Control
                type="text"
                name="role"
                value={filters.role}
                onChange={onFilterChange}
                placeholder="Filter Role"
                className="mt-1 form-control-sm"
              />
            </th>
          </tr>
        </thead>
        <tbody> {/* Replaced bg-white divide-y divide-gray-200 */}
          {users.map((user) => (
            <tr key={user.id}> {/* Replaced hover:bg-gray-50 */}
              <td className="text-start fw-medium">{user.name}</td> {/* Replaced px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 */}
              <td className="text-start text-muted">{user.email}</td> {/* Replaced px-6 py-4 whitespace-nowrap text-sm text-gray-500 */}
              <td className="text-start text-muted">{user.address}</td> {/* Replaced px-6 py-4 whitespace-nowrap text-sm text-gray-500 */}
              <td className="text-start text-muted">{user.role}</td> {/* Replaced px-6 py-4 whitespace-nowrap text-sm text-gray-500 */}
            </tr>
          ))}
          {users.length === 0 && (
            <tr>
              <td colSpan="4" className="text-center text-muted">No users found.</td> {/* Replaced px-6 py-4 text-center text-gray-500 */}
            </tr>
          )}
        </tbody>
      </Table>
    </Card>
  );
}

export default UserTable;
