import React from 'react';

/**
 * StoreTable Component:
 * Displays a sortable and filterable table of stores.
 * @param {Array} stores - Array of store objects to display.
 * @param {Object} filters - Current filter values for each column.
 * @param {Function} onFilterChange - Callback function for filter input changes.
 * @param {Function} onSort - Callback function for sorting column clicks.
 * @param {Object} sortConfig - Current sort configuration (key and direction).
 */
function StoreTable({ stores, filters, onFilterChange, onSort, sortConfig }) {
  // Helper function to display sort indicator (arrow)
  const getSortIndicator = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? ' ▲' : ' ▼';
    }
    return '';
  };

  return (
    <div className="bg-white rounded-xl shadow-lg p-6 overflow-x-auto">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            {/* Table Headers with Sort Buttons and Filter Inputs */}
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              <div className="flex items-center">
                Name
                <button onClick={() => onSort('name')} className="ml-1 focus:outline-none">
                  {getSortIndicator('name')}
                </button>
              </div>
              <input
                type="text"
                name="name"
                value={filters.name}
                onChange={onFilterChange}
                placeholder="Filter Name"
                className="mt-1 block w-full px-2 py-1 border border-gray-300 rounded-md text-sm"
              />
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              <div className="flex items-center">
                Email
                <button onClick={() => onSort('email')} className="ml-1 focus:outline-none">
                  {getSortIndicator('email')}
                </button>
              </div>
              <input
                type="text"
                name="email"
                value={filters.email}
                onChange={onFilterChange}
                placeholder="Filter Email"
                className="mt-1 block w-full px-2 py-1 border border-gray-300 rounded-md text-sm"
              />
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              <div className="flex items-center">
                Address
                <button onClick={() => onSort('address')} className="ml-1 focus:outline-none">
                  {getSortIndicator('address')}
                </button>
              </div>
              <input
                type="text"
                name="address"
                value={filters.address}
                onChange={onFilterChange}
                placeholder="Filter Address"
                className="mt-1 block w-full px-2 py-1 border border-gray-300 rounded-md text-sm"
              />
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              <div className="flex items-center">
                Rating
                <button onClick={() => onSort('rating')} className="ml-1 focus:outline-none">
                  {getSortIndicator('rating')}
                </button>
              </div>
            </th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {stores.map((store) => (
            <tr key={store.id} className="hover:bg-gray-50">
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{store.name}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{store.email}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{store.address}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                {/* Robust check for rating: ensure it's a valid number or can be parsed to one */}
                {typeof store.rating === 'number' && !isNaN(store.rating)
                  ? store.rating.toFixed(2)
                  : (typeof store.rating === 'string' && !isNaN(parseFloat(store.rating))
                      ? parseFloat(store.rating).toFixed(2)
                      : 'N/A')}
              </td>
            </tr>
          ))}
          {stores.length === 0 && (
            <tr>
              <td colSpan="4" className="px-6 py-4 text-center text-gray-500">No stores found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default StoreTable;
