import React, { useState, useContext, useEffect, useCallback } from 'react';
import { AuthContext } from '../AuthContext';
import UpdatePasswordModal from '../components/UpdatePasswordModal';
import { Container, Row, Col, Card, Button, Table, Alert } from 'react-bootstrap'; // Import Bootstrap components

/**
 * StoreOwnerDashboard Component:
 * Displays dashboard functionalities for store owners,
 * including average store rating and a list of users who submitted ratings.
 */
function StoreOwnerDashboard() {
  // --- React Hooks must be called unconditionally at the top level ---
  const authContext = useContext(AuthContext);
  const [storeRatings, setStoreRatings] = useState([]);
  const [usersWhoRatedStores, setUsersWhoRatedStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showUpdatePasswordModal, setShowUpdatePasswordModal] = useState(false);

  // Memoize fetchStoreOwnerRatings using useCallback
  const fetchStoreOwnerRatings = useCallback(async () => {
    setLoading(true);
    setError('');
    // Ensure token is available before making API call
    if (!authContext || !authContext.token) {
      setError('Authentication token not found. Please log in.');
      setLoading(false);
      return;
    }

    try {
      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${authContext.token}`,
      };

      // Fetch both average store ratings and users who rated
      const [storeRatingsRes, usersRatedRes] = await Promise.all([
        fetch('http://localhost:5000/api/ratings/owner', { headers }),
        fetch('http://localhost:5000/api/ratings/owner/users-rated', { headers }),
      ]);

      if (!storeRatingsRes.ok || !usersRatedRes.ok) {
        const storeError = !storeRatingsRes.ok ? `Store ratings fetch failed: ${storeRatingsRes.statusText}` : '';
        const usersError = !usersRatedRes.ok ? `Users rated fetch failed: ${usersRatedRes.statusText}` : '';
        throw new Error(`Failed to fetch dashboard data. ${storeError} ${usersError}`);
      }

      const storeRatingsData = await storeRatingsRes.json();
      const usersRatedData = await usersRatedRes.json();

      setStoreRatings(storeRatingsData);
      setUsersWhoRatedStores(usersRatedData);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [authContext]);

  useEffect(() => {
    if (authContext && authContext.token) {
      fetchStoreOwnerRatings();
    } else {
      setLoading(false);
      setError('Not authenticated. Please log in.');
    }
  }, [authContext, fetchStoreOwnerRatings]);
  // --- End of React Hooks section ---


  // Defensive check for authContext after all hooks are called
  if (!authContext) {
    console.error("AuthContext is null in StoreOwnerDashboard. Ensure it's rendered within AuthProvider.");
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <p className="h4 text-muted">Authentication context not available. Please log in again.</p>
      </Container>
    );
  }

  const { token, logout } = authContext;


  if (loading) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <p className="h4 text-muted">Loading dashboard...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <Alert variant="danger" className="h4 text-center">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container fluid className="py-4 bg-light min-vh-100"> {/* Replaced p-8 bg-gray-50 min-h-screen */}
      <header className="d-flex justify-content-between align-items-center mb-4 pb-3 border-bottom"> {/* Replaced flex justify-between items-center mb-10 pb-4 border-b border-gray-200 */}
        <h1 className="h2 text-dark">Store Owner Dashboard</h1> {/* Replaced text-4xl font-extrabold text-gray-900 */}
        <div className="d-flex align-items-center"> {/* Replaced flex items-center space-x-4 */}
          <Button
            variant="warning" // Replaced bg-yellow-600
            onClick={() => setShowUpdatePasswordModal(true)}
            className="me-2" // Replaced ml-4
          >
            Update Password
          </Button>
          <Button
            variant="danger" // Replaced bg-red-600
            onClick={logout}
          >
            Logout
          </Button>
        </div>
      </header>

      <section className="mb-4"> {/* Replaced mb-12 */}
        <h2 className="h3 text-dark mb-3">Your Stores' Ratings Overview</h2> {/* Replaced text-3xl font-bold text-gray-900 mb-6 */}
        {storeRatings.length === 0 ? (
          <div className="card shadow-sm p-4 text-center text-muted"> {/* Replaced bg-white p-6 rounded-xl shadow-lg text-center text-gray-600 */}
            No stores found or no ratings submitted for your stores yet.
          </div>
        ) : (
          <Row className="g-4"> {/* Replaced grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 */}
            {storeRatings.map((store) => (
              <Col md={4} key={store.name}> {/* Replaced div with Col, added key */}
                <Card className="shadow-sm p-4 text-center"> {/* Replaced bg-white p-6 rounded-xl shadow-lg flex flex-col items-center justify-center transform transition-transform hover:scale-105 */}
                  <Card.Title className="h4 text-dark mb-2">{store.name}</Card.Title> {/* Replaced h3 text-2xl font-semibold text-gray-800 mb-2 */}
                  <Card.Text className="h5 text-muted mb-2">Average Rating:</Card.Text> {/* Replaced p text-xl text-gray-600 mb-4 */}
                  <Card.Text className="display-4 text-primary"> {/* Replaced p text-5xl font-bold text-blue-600 */}
                    {typeof store.avg_rating === 'number' && !isNaN(store.avg_rating)
                      ? store.avg_rating.toFixed(2)
                      : (typeof store.avg_rating === 'string' && !isNaN(parseFloat(store.avg_rating))
                          ? parseFloat(store.avg_rating).toFixed(2)
                          : 'N/A')}
                  </Card.Text>
                  <Card.Text className="text-muted">({store.total_ratings} total ratings)</Card.Text> {/* Replaced p text-md text-gray-500 mt-2 */}
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </section>

      <section>
        <h2 className="h3 text-dark mb-3">Users Who Rated Your Stores</h2> {/* Replaced text-3xl font-bold text-gray-900 mb-6 */}
        {usersWhoRatedStores.length === 0 ? (
          <div className="card shadow-sm p-4 text-center text-muted"> {/* Replaced bg-white p-6 rounded-xl shadow-lg text-center text-gray-600 */}
            No users have submitted ratings for your stores yet.
          </div>
        ) : (
          <div className="card shadow-sm p-3"> {/* Replaced bg-white rounded-xl shadow-lg p-6 overflow-x-auto */}
            <Table striped bordered hover responsive className="mb-0"> {/* Replaced min-w-full divide-y divide-gray-200 */}
              <thead className="bg-light"> {/* Replaced bg-gray-50 */}
                <tr>
                  <th>User Name</th> {/* Replaced th px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider */}
                  <th>User Email</th>
                  <th>Store Rated</th>
                  <th>Rating</th>
                </tr>
              </thead>
              <tbody> {/* Replaced bg-white divide-y divide-gray-200 */}
                {usersWhoRatedStores.map((ratingEntry, index) => (
                  <tr key={index}> {/* Replaced hover:bg-gray-50 */}
                    <td>{ratingEntry.user_name}</td> {/* Replaced td px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 */}
                    <td>{ratingEntry.user_email}</td>
                    <td>{ratingEntry.store_name}</td>
                    <td>{ratingEntry.rating}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        )}
      </section>

      {showUpdatePasswordModal && (
        <UpdatePasswordModal
          onClose={() => setShowUpdatePasswordModal(false)}
        />
      )}
    </Container>
  );
}

export default StoreOwnerDashboard;
