import React, { useState, useContext } from 'react';
import { AuthContext } from '../AuthContext'; // Import AuthContext
import { Form, Button, Alert, Card } from 'react-bootstrap'; // Import Bootstrap components

/**
 * LoginPage Component:
 * Handles user login with email and password.
 * On successful login, it updates the AuthContext.
 */
function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext);

  // Handles the form submission for login
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        // If response is not OK, parse error message from backend
        const errorData = await response.json();
        throw new Error(errorData.message || 'Invalid credentials');
      }

      const data = await response.json();
      login(data.token, data.role); // Update auth context on successful login
    } catch (err) {
      setError(err.message); // Display error message
    }
  };

  return (
    // Added w-100 to ensure full width
    <div className="d-flex flex-column align-items-center justify-content-center min-vh-100 bg-primary bg-gradient p-3 w-100">
      <Card className="p-4 shadow-lg" style={{ maxWidth: '400px', width: '100%' }}>
        <Card.Title className="text-center mb-4 h3">Login</Card.Title>
        {error && <Alert variant="danger" className="text-center">{error}</Alert>}
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label htmlFor="email">Email</Form.Label>
            <Form.Control
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group className="mb-4">
            <Form.Label htmlFor="password">Password</Form.Label>
            <Form.Control
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit" className="w-100 py-2">
            Sign In
          </Button>
        </Form>
      </Card>
      {/* <p className="mt-3 text-muted text-center">
        Don't have an account?{' '}
        <a href="#register" className="text-white text-decoration-none" onClick={(e) => {
          e.preventDefault();
          // This logic is handled by the parent App.js, but keeping for clarity if used standalone
          window.location.hash = '#register';
        }}>
          Register here
        </a>
      </p> */}
    </div>
  );
}

export default LoginPage;
