import React, { useState } from 'react';
import { Modal, Button, Form, Alert } from 'react-bootstrap'; // Import Bootstrap components
import { validateForm } from '../utils/validation'; // Import validation utility

/**
 * AddUserModal Component:
 * A modal form for administrators to add new users.
 * Includes client-side validation for input fields.
 * @param {Function} onClose - Callback to close the modal.
 * @param {Function} onUserAdded - Callback to refresh user list after adding a user.
 * @param {string} token - JWT token for API authentication.
 */
function AddUserModal({ onClose, onUserAdded, token }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    address: '',
    role: 'user', // Default role for new users added by admin
  });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Handles changes in form input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    // Clear error for the current field as user types
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: null }));
    }
  };

  // Handles form submission for adding a new user
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(''); // Clear previous messages

    const newErrors = validateForm(formData, 'user');
    setErrors(newErrors);

    if (Object.keys(newErrors).length > 0) {
      setMessage('Please correct the form errors.');
      return;
    }

    setLoading(true);
    try {
      // API call to create a new user
      const response = await fetch('http://localhost:5000/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Send JWT token for authentication
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to add user.');
      }

      setMessage('User added successfully!');
      onUserAdded(); // Callback to refresh user list in AdminDashboard
      setTimeout(onClose, 1500); // Close modal after a short delay
    } catch (err) {
      setMessage(`Error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Modal show={true} onHide={onClose} centered> {/* Replaced fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50 */}
      <Modal.Header closeButton>
        <Modal.Title>Add New User</Modal.Title> {/* Replaced h2 text-2xl font-bold text-gray-900 mb-6 text-center */}
      </Modal.Header>
      <Modal.Body>
        {message && (
          <Alert variant={message.startsWith('Error') ? 'danger' : 'success'}> {/* Replaced p tag with Alert */}
            {message}
          </Alert>
        )}
        <Form onSubmit={handleSubmit}> {/* Replaced form with Form component */}
          <Form.Group className="mb-3"> {/* Replaced div with Form.Group */}
            <Form.Label htmlFor="name">Name</Form.Label> {/* Replaced label with Form.Label */}
            <Form.Control
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              isInvalid={!!errors.name}
              required
            /> {/* Replaced input with Form.Control */}
            <Form.Control.Feedback type="invalid">{errors.name}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label htmlFor="email">Email</Form.Label>
            <Form.Control
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              isInvalid={!!errors.email}
              required
            />
            <Form.Control.Feedback type="invalid">{errors.email}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label htmlFor="password">Password</Form.Label>
            <Form.Control
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              isInvalid={!!errors.password}
              required
            />
            <Form.Control.Feedback type="invalid">{errors.password}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label htmlFor="address">Address</Form.Label>
            <Form.Control
              as="textarea"
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
              rows="3"
              isInvalid={!!errors.address}
              required
            /> {/* Replaced textarea with Form.Control as="textarea" */}
            <Form.Control.Feedback type="invalid">{errors.address}</Form.Control.Feedback>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label htmlFor="role">Role</Form.Label>
            <Form.Select
              id="role"
              name="role"
              value={formData.role}
              onChange={handleChange}
            > {/* Replaced select with Form.Select */}
              <option value="user">Normal User</option>
              <option value="store_owner">Store Owner</option>
              <option value="admin">System Administrator</option>
            </Form.Select>
          </Form.Group>
          <div className="d-flex justify-content-end"> {/* Replaced flex justify-end space-x-4 mt-6 */}
            <Button
              variant="secondary" // Replaced bg-gray-300
              onClick={onClose}
              className="me-2" // Replaced mr-2
              disabled={loading}
            >
              Cancel
            </Button>
            <Button
              variant="primary" // Replaced bg-blue-600
              type="submit"
              disabled={loading}
            >
              {loading ? 'Adding...' : 'Add User'}
            </Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default AddUserModal;
