import React, { useState, useContext } from 'react';
import { AuthContext } from '../AuthContext'; // Import AuthContext
import { Form, Button, Alert, Card } from 'react-bootstrap'; // Import Bootstrap components

/**
 * LoginPage Component:
 * Handles user login with email and password.
 * On successful login, it updates the AuthContext.
 */
function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext);

  // Handles the form submission for login
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        // If response is not OK, parse error message from backend
        const errorData = await response.json();
        throw new Error(errorData.message || 'Invalid credentials');
      }

      const data = await response.json();
      login(data.token, data.role); // Update auth context on successful login
    } catch (err) {
      setError(err.message); // Display error message
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center min-vh-100 bg-primary bg-gradient"> {/* Replaced flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-500 to-indigo-600 */}
      <Card className="p-4 shadow-lg" style={{ maxWidth: '400px', width: '100%' }}> {/* Replaced bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-300 hover:scale-105 */}
        <Card.Title className="text-center mb-4 h3">Login</Card.Title> {/* Replaced text-3xl font-extrabold text-center text-gray-900 mb-8 */}
        {error && <Alert variant="danger" className="text-center">{error}</Alert>} {/* Replaced text-red-600 text-center mb-4 font-medium */}
        <Form onSubmit={handleSubmit}> {/* Replaced space-y-6 */}
          <Form.Group className="mb-3">
            <Form.Label htmlFor="email">Email</Form.Label> {/* Replaced block text-sm font-medium text-gray-700 mb-1 */}
            <Form.Control
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            /> {/* Replaced mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500 transition duration-200 */}
          </Form.Group>
          <Form.Group className="mb-4"> {/* Increased mb-3 to mb-4 for more space */}
            <Form.Label htmlFor="password">Password</Form.Label>
            <Form.Control
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit" className="w-100 py-2"> {/* Replaced w-full py-3 px-4 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition duration-300 ease-in-out transform hover:-translate-y-0.5 */}
            Sign In
          </Button>
        </Form>
      </Card>
    </div>
  );
}

export default LoginPage;
