import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../AuthContext'; // Import AuthContext
import UpdatePasswordModal from '../components/UpdatePasswordModal';

/**
 * UserDashboard Component:
 * Allows normal users to view stores, search, submit, and modify ratings.
 */
function UserDashboard() {
  const { token, logout } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState({ name: '', address: '' });
  const [userRatings, setUserRatings] = useState({}); // Stores user's submitted ratings for quick lookup
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);
  const [currentRating, setCurrentRating] = useState(0);
  const [ratingMessage, setRatingMessage] = useState('');
  const [showUpdatePasswordModal, setShowUpdatePasswordModal] = useState(false); // <--- ADDED: State for password modal

  // Fetches all stores and the user's submitted ratings
  const fetchStoresAndRatings = async () => {
    setLoading(true);
    setError('');
    try {
      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      };

      const [storesRes, userRatingsRes] = await Promise.all([
        fetch('http://localhost:5000/api/stores', { headers }),
        fetch('http://localhost:5000/api/ratings/user', { headers }),
      ]);

      if (!storesRes.ok || !userRatingsRes.ok) {
        throw new Error('Failed to fetch data. Please check server status.');
      }

      const storesData = await storesRes.json();
      const userRatingsData = await userRatingsRes.json();

      setStores(storesData);

      // Map user ratings for easy access: { store_id: rating }
      const ratingsMap = userRatingsData.reduce((acc, rating) => {
        acc[rating.store_id] = rating.rating;
        return acc;
      }, {});
      setUserRatings(ratingsMap);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStoresAndRatings();
  }, [token]);

  // Handles search input changes
  const handleSearchChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery((prev) => ({ ...prev, [name]: value }));
  };

  // Filters stores based on search query
  const filteredStores = stores.filter((store) => {
    return (
      store.name.toLowerCase().includes(searchQuery.name.toLowerCase()) &&
      store.address.toLowerCase().includes(searchQuery.address.toLowerCase())
    );
  });

  // Opens the rating modal for a specific store
  const openRatingModal = (store) => {
    setSelectedStore(store);
    setCurrentRating(userRatings[store.id] || 0); // Set current rating if already submitted
    setRatingMessage(''); // Clear previous messages
    setShowRatingModal(true);
  };

  // Submits a rating for a store
  const handleSubmitRating = async () => {
    if (!selectedStore || currentRating < 1 || currentRating > 5) {
      setRatingMessage('Please select a store and a rating between 1 and 5.');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/ratings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ store_id: selectedStore.id, rating: currentRating }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to submit rating.');
      }

      setRatingMessage('Rating submitted successfully!');
      // Refresh data after successful submission
      await fetchStoresAndRatings();
      setTimeout(() => setShowRatingModal(false), 1500); // Close modal after a short delay
    } catch (err) {
      setRatingMessage(`Error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center h-screen text-2xl text-gray-700">Loading stores...</div>;
  }

  if (error) {
    return <div className="flex justify-center items-center h-screen text-2xl text-red-600">{error}</div>;
  }

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <header className="flex justify-between items-center mb-10 pb-4 border-b border-gray-200">
        <h1 className="text-4xl font-extrabold text-gray-900">User Dashboard</h1>
        <div className="flex items-center space-x-4">
          <button
            onClick={() => setShowUpdatePasswordModal(true)}
            className="py-2 px-6 bg-yellow-600 text-white font-semibold rounded-lg shadow-md hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-yellow-500 focus:ring-offset-2 transition duration-300"
          >
            Update Password
          </button>
          <button
            onClick={logout}
            className="py-2 px-6 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 transition duration-300"
          >
            Logout
          </button>
        </div>
      </header>

      <section className="mb-8 bg-white p-6 rounded-xl shadow-lg">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Search Stores</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="searchName">Store Name</label>
            <input
              type="text"
              id="searchName"
              name="name"
              value={searchQuery.name}
              onChange={handleSearchChange}
              placeholder="Search by name"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="searchAddress">Address</label>
            <input
              type="text"
              id="searchAddress"
              name="address"
              value={searchQuery.address}
              onChange={handleSearchChange}
              placeholder="Search by address"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
        </div>
      </section>

      <section>
        <h2 className="text-3xl font-bold text-gray-900 mb-6">All Stores</h2>
        <div className="bg-white rounded-xl shadow-lg p-6 overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Store Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Address</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Overall Rating</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Your Rating</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredStores.map((store) => (
                <tr key={store.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{store.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{store.address}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {/* Robust check for rating: ensure it's a valid number or can be parsed to one */}
                    {typeof store.rating === 'number' && !isNaN(store.rating)
                      ? store.rating.toFixed(2)
                      : (typeof store.rating === 'string' && !isNaN(parseFloat(store.rating))
                          ? parseFloat(store.rating).toFixed(2)
                          : 'N/A')}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {userRatings[store.id] || 'Not rated'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <button
                      onClick={() => openRatingModal(store)}
                      className="py-1 px-3 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition duration-200"
                    >
                      {userRatings[store.id] ? 'Modify Rating' : 'Submit Rating'}
                    </button>
                  </td>
                </tr>
              ))}
              {filteredStores.length === 0 && (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">No stores found.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </section>

      {/* Modals */}
      {showRatingModal && selectedStore && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-300 scale-100">
            <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">
              Rate {selectedStore.name}
            </h2>
            <div className="mb-4 text-center">
              <label className="block text-lg font-medium text-gray-700 mb-2">Your Rating (1-5)</label>
              <input
                type="number"
                min="1"
                max="5"
                value={currentRating}
                onChange={(e) => setCurrentRating(parseInt(e.target.value) || 0)}
                className="w-24 text-center px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500 text-xl"
              />
            </div>
            {ratingMessage && (
              <p className={`text-center text-sm font-medium mb-4 ${ratingMessage.startsWith('Error') ? 'text-red-600' : 'text-green-600'}`}>
                {ratingMessage}
              </p>
            )}
            <div className="flex justify-end space-x-4 mt-6">
              <button
                type="button"
                onClick={() => setShowRatingModal(false)}
                className="py-2 px-5 bg-gray-300 text-gray-800 font-semibold rounded-lg shadow-md hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 transition duration-300"
                disabled={loading}
              >
                Cancel
              </button>
              <button
                type="submit"
                onClick={handleSubmitRating}
                className="py-2 px-5 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 transition duration-300"
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Submit Rating'}
              </button>
            </div>
          </div>
        </div>
      )}

      {showUpdatePasswordModal && (
        <UpdatePasswordModal
          onClose={() => setShowUpdatePasswordModal(false)}
        />
      )}
    </div>
  );
}

export default UserDashboard;
