import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../AuthContext'; // Import AuthContext
import UserTable from '../components/UserTable';
import StoreTable from '../components/StoreTable';
import AddUserModal from '../components/AddUserModal';
import AddStoreModal from '../components/AddStoreModal';
import UpdatePasswordModal from '../components/UpdatePasswordModal'; // Import new modal
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap'; // Import Bootstrap components

/**
 * AdminDashboard Component:
 * The main dashboard for administrators.
 * Displays overall statistics, user management, and store management sections.
 */
function AdminDashboard() {
  const { token, role, logout } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [ratingsCount, setRatingsCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [showAddUserModal, setShowAddUserModal] = useState(false);
  const [showAddStoreModal, setShowAddStoreModal] = useState(false);
  const [showUpdatePasswordModal, setShowUpdatePasswordModal] = useState(false);

  // States for user table filtering and sorting
  const [userFilters, setUserFilters] = useState({ name: '', email: '', address: '', role: '' });
  const [userSortConfig, setUserSortConfig] = useState({ key: null, direction: 'ascending' });

  // States for store table filtering and sorting
  const [storeFilters, setStoreFilters] = useState({ name: '', email: '', address: '' });
  const [storeSortConfig, setStoreSortConfig] = useState({ key: null, direction: 'ascending' });

  // Fetches all necessary dashboard data from the backend
  const fetchDashboardData = async () => {
    setLoading(true);
    setError('');
    try {
      if (role !== 'admin') {
        setError('Access Denied: Not an admin.');
        setLoading(false);
        return;
      }

      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      };

      // Fetch data concurrently using Promise.all
      const [usersRes, storesRes, ratingsCountRes] = await Promise.all([
        fetch('http://localhost:5000/api/admin/users', { headers }),
        fetch('http://localhost:5000/api/admin/stores', { headers }),
        fetch('http://localhost:5000/api/admin/ratings/count', { headers }),
      ]);

      if (!usersRes.ok || !storesRes.ok || !ratingsCountRes.ok) {
        throw new Error('Failed to fetch dashboard data. Please check server status.');
      }

      const usersData = await usersRes.json();
      const storesData = await storesRes.json();
      const ratingsCountData = await ratingsCountRes.json();

      setUsers(usersData);
      setStores(storesData);
      setRatingsCount(ratingsCountData.count);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Effect hook to fetch data when component mounts or token/role changes
  useEffect(() => {
    fetchDashboardData();
  }, [token, role]); // Dependency array ensures refetch on token/role change

  // Handles user logout
  const handleLogout = () => {
    logout();
  };

  // --- User Table Filtering and Sorting Logic ---
  const handleUserFilterChange = (e) => {
    const { name, value } = e.target;
    setUserFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleUserSort = (key) => {
    let direction = 'ascending';
    if (userSortConfig.key === key && userSortConfig.direction === 'ascending') {
      direction = 'descending'; // Toggle direction if same key is clicked again
    }
    setUserSortConfig({ key, direction });
  };

  // Applies filters and sorting to the users data
  const filteredAndSortedUsers = [...users]
    .filter((user) => {
      return (
        user.name.toLowerCase().includes(userFilters.name.toLowerCase()) &&
        user.email.toLowerCase().includes(userFilters.email.toLowerCase()) &&
        user.address.toLowerCase().includes(userFilters.address.toLowerCase()) &&
        user.role.toLowerCase().includes(userFilters.role.toLowerCase())
      );
    })
    .sort((a, b) => {
      if (userSortConfig.key) {
        const aValue = a[userSortConfig.key];
        const bValue = b[userSortConfig.key];

        // Handle string comparison for sorting
        if (typeof aValue === 'string' && typeof bValue === 'string') {
          return userSortConfig.direction === 'ascending' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
        }
        // Handle numeric comparison for sorting
        if (aValue < bValue) {
          return userSortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (aValue > bValue) {
          return userSortConfig.direction === 'ascending' ? 1 : -1;
        }
      }
      return 0; // No sorting if key is not set
    });

  // --- Store Table Filtering and Sorting Logic ---
  const handleStoreFilterChange = (e) => {
    const { name, value } = e.target;
    setStoreFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleStoreSort = (key) => {
    let direction = 'ascending';
    if (storeSortConfig.key === key && storeSortConfig.direction === 'ascending') {
      direction = 'descending'; // Toggle direction if same key is clicked again
    }
    setStoreSortConfig({ key, direction });
  };

  // Applies filters and sorting to the stores data
  const filteredAndSortedStores = [...stores]
    .filter((store) => {
      return (
        store.name.toLowerCase().includes(storeFilters.name.toLowerCase()) &&
        store.email.toLowerCase().includes(storeFilters.email.toLowerCase()) &&
        store.address.toLowerCase().includes(storeFilters.address.toLowerCase())
      );
    })
    .sort((a, b) => {
      if (storeSortConfig.key) {
        const aValue = a[storeSortConfig.key];
        const bValue = b[storeSortConfig.key];

        // Handle string comparison for sorting
        if (typeof aValue === 'string' && typeof bValue === 'string') {
          return storeSortConfig.direction === 'ascending' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
        }
        // Handle numeric comparison for sorting (e.g., for 'rating')
        if (aValue < bValue) {
          return storeSortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (aValue > bValue) {
          return storeSortConfig.direction === 'ascending' ? 1 : -1;
        }
      }
      return 0; // No sorting if key is not set
    });

  // Display loading message while data is being fetched
  if (loading) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <p className="h4 text-muted">Loading dashboard...</p>
      </Container>
    );
  }

  // Display error message if data fetching fails
  if (error) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <Alert variant="danger" className="h4 text-center">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container fluid className="py-4 bg-light min-vh-100"> {/* Replaced p-8 bg-gray-50 min-h-screen */}
      <header className="d-flex justify-content-between align-items-center mb-4 pb-3 border-bottom"> {/* Replaced flex justify-between items-center mb-10 pb-4 border-b border-gray-200 */}
        <h1 className="h2 text-dark">Admin Dashboard</h1> {/* Replaced text-4xl font-extrabold text-gray-900 */}
        <div className="d-flex align-items-center"> {/* Replaced flex items-center space-x-4 */}
          {/* Update Password Button */}
          <Button
            variant="warning" // Replaced bg-yellow-600
            onClick={() => setShowUpdatePasswordModal(true)}
            className="me-2" // Replaced ml-4
          >
            Update Password
          </Button>
          <Button
            variant="danger" // Replaced bg-red-600
            onClick={handleLogout}
          >
            Logout
          </Button>
        </div>
      </header>

      {/* Dashboard Statistics Section */}
      <Row className="mb-4 g-4"> {/* Replaced grid grid-cols-1 md:grid-cols-3 gap-8 mb-12 */}
        <Col md={4}> {/* Replaced div */}
          <Card className="shadow-sm p-4 text-center"> {/* Replaced bg-white p-6 rounded-xl shadow-lg flex flex-col items-center justify-center transform transition-transform hover:scale-105 */}
            <Card.Title className="h5 text-muted mb-2">Total Users</Card.Title> {/* Replaced text-xl font-semibold text-gray-700 mb-2 */}
            <p className="display-4 text-primary">{users.length}</p> {/* Replaced text-5xl font-bold text-blue-600 */}
          </Card>
        </Col>
        <Col md={4}>
          <Card className="shadow-sm p-4 text-center">
            <Card.Title className="h5 text-muted mb-2">Total Stores</Card.Title>
            <p className="display-4 text-success">{stores.length}</p>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="shadow-sm p-4 text-center">
            <Card.Title className="h5 text-muted mb-2">Total Ratings</Card.Title>
            <p className="display-4 text-info">{ratingsCount}</p>
          </Card>
        </Col>
      </Row>

      {/* User Management Section */}
      <section className="mb-4"> {/* Replaced mb-12 */}
        <div className="d-flex justify-content-between align-items-center mb-3"> {/* Replaced flex justify-between items-center mb-6 */}
          <h2 className="h3 text-dark">Manage Users</h2> {/* Replaced text-3xl font-bold text-gray-900 */}
          <Button
            variant="primary" // Replaced bg-blue-600
            onClick={() => setShowAddUserModal(true)}
          >
            Add New User
          </Button>
        </div>
        <UserTable
          users={filteredAndSortedUsers}
          filters={userFilters}
          onFilterChange={handleUserFilterChange}
          onSort={handleUserSort}
          sortConfig={userSortConfig}
        />
      </section>

      {/* Store Management Section */}
      <section className="mb-4">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h2 className="h3 text-dark">Manage Stores</h2>
          <Button
            variant="success" // Replaced bg-green-600
            onClick={() => setShowAddStoreModal(true)}
          >
            Add New Store
          </Button>
        </div>
        <StoreTable
          stores={filteredAndSortedStores}
          filters={storeFilters}
          onFilterChange={handleStoreFilterChange}
          onSort={handleStoreSort}
          sortConfig={storeSortConfig}
        />
      </section>

      {/* Modals */}
      {showAddUserModal && (
        <AddUserModal
          onClose={() => setShowAddUserModal(false)}
          onUserAdded={fetchDashboardData} // Callback to refetch data after adding user
          token={token}
        />
      )}

      {showAddStoreModal && (
        <AddStoreModal
          onClose={() => setShowAddStoreModal(false)}
          onStoreAdded={fetchDashboardData} // Callback to refetch data after adding store
          token={token}
          // Pass only users with 'user' or 'store_owner' roles as potential owners
          users={users.filter(u => u.role === 'store_owner' || u.role === 'user')}
        />
      )}

      {showUpdatePasswordModal && (
        <UpdatePasswordModal
          onClose={() => setShowUpdatePasswordModal(false)}
        />
      )}
    </Container>
  );
}

export default AdminDashboard;
