import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../AuthContext'; // Import AuthContext
import UpdatePasswordModal from '../components/UpdatePasswordModal';
import { Container, Row, Col, Form, Button, Table, Alert, Card, Modal } from 'react-bootstrap'; // Import Bootstrap components

/**
 * UserDashboard Component:
 * Allows normal users to view stores, search, submit, and modify ratings.
 */
function UserDashboard() {
  const { token, logout } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState({ name: '', address: '' });
  const [userRatings, setUserRatings] = useState({}); // Stores user's submitted ratings for quick lookup
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [selectedStore, setSelectedStore] = useState(null);
  const [currentRating, setCurrentRating] = useState(0);
  const [ratingMessage, setRatingMessage] = useState('');
  const [showUpdatePasswordModal, setShowUpdatePasswordModal] = useState(false); // State for password modal

  // Fetches all stores and the user's submitted ratings
  const fetchStoresAndRatings = async () => {
    setLoading(true);
    setError('');
    try {
      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      };

      const [storesRes, userRatingsRes] = await Promise.all([
        fetch('http://localhost:5000/api/stores', { headers }),
        fetch('http://localhost:5000/api/ratings/user', { headers }),
      ]);

      if (!storesRes.ok || !userRatingsRes.ok) {
        throw new Error('Failed to fetch data. Please check server status.');
      }

      const storesData = await storesRes.json();
      const userRatingsData = await userRatingsRes.json();

      setStores(storesData);

      // Map user ratings for easy access: { store_id: rating }
      const ratingsMap = userRatingsData.reduce((acc, rating) => {
        acc[rating.store_id] = rating.rating;
        return acc;
      }, {});
      setUserRatings(ratingsMap);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStoresAndRatings();
  }, [token]);

  // Handles search input changes
  const handleSearchChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery((prev) => ({ ...prev, [name]: value }));
  };

  // Filters stores based on search query
  const filteredStores = stores.filter((store) => {
    return (
      store.name.toLowerCase().includes(searchQuery.name.toLowerCase()) &&
      store.address.toLowerCase().includes(searchQuery.address.toLowerCase())
    );
  });

  // Opens the rating modal for a specific store
  const openRatingModal = (store) => {
    setSelectedStore(store);
    setCurrentRating(userRatings[store.id] || 0); // Set current rating if already submitted
    setRatingMessage(''); // Clear previous messages
    setShowRatingModal(true);
  };

  // Submits a rating for a store
  const handleSubmitRating = async () => {
    if (!selectedStore || currentRating < 1 || currentRating > 5) {
      setRatingMessage('Please select a store and a rating between 1 and 5.');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/ratings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ store_id: selectedStore.id, rating: currentRating }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to submit rating.');
      }

      setRatingMessage('Rating submitted successfully!');
      // Refresh data after successful submission
      await fetchStoresAndRatings();
      setTimeout(() => setShowRatingModal(false), 1500); // Close modal after a short delay
    } catch (err) {
      setRatingMessage(`Error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <p className="h4 text-muted">Loading stores...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <Alert variant="danger" className="h4 text-center">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container fluid className="py-4 bg-light min-vh-100"> {/* Replaced p-8 bg-gray-50 min-h-screen */}
      <header className="d-flex justify-content-between align-items-center mb-4 pb-3 border-bottom"> {/* Replaced flex justify-between items-center mb-10 pb-4 border-b border-gray-200 */}
        <h1 className="h2 text-dark">User Dashboard</h1> {/* Replaced text-4xl font-extrabold text-gray-900 */}
        <div className="d-flex align-items-center"> {/* Replaced flex items-center space-x-4 */}
          <Button
            variant="warning" // Replaced bg-yellow-600
            onClick={() => setShowUpdatePasswordModal(true)}
            className="me-2" // Replaced ml-4
          >
            Update Password
          </Button>
          <Button
            variant="danger" // Replaced bg-red-600
            onClick={logout}
          >
            Logout
          </Button>
        </div>
      </header>

      <section className="mb-4 card shadow-sm p-3"> {/* Replaced mb-8 bg-white p-6 rounded-xl shadow-lg */}
        <h2 className="h4 card-title mb-3">Search Stores</h2> {/* Replaced text-2xl font-bold text-gray-900 mb-4 */}
        <Form>
          <Row className="g-3"> {/* Replaced grid grid-cols-1 md:grid-cols-2 gap-4 */}
            <Col md={6}>
              <Form.Group>
                <Form.Label htmlFor="searchName">Store Name</Form.Label> {/* Replaced block text-sm font-medium text-gray-700 mb-1 */}
                <Form.Control
                  type="text"
                  id="searchName"
                  name="name"
                  value={searchQuery.name}
                  onChange={handleSearchChange}
                  placeholder="Search by name"
                /> {/* Replaced mt-1 block w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500 */}
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group>
                <Form.Label htmlFor="searchAddress">Address</Form.Label>
                <Form.Control
                  type="text"
                  id="searchAddress"
                  name="address"
                  value={searchQuery.address}
                  onChange={handleSearchChange}
                  placeholder="Search by address"
                />
              </Form.Group>
            </Col>
          </Row>
        </Form>
      </section>

      <section>
        <h2 className="h3 text-dark mb-3">All Stores</h2> {/* Replaced text-3xl font-bold text-gray-900 mb-6 */}
        <div className="card shadow-sm p-3"> {/* Replaced bg-white rounded-xl shadow-lg p-6 overflow-x-auto */}
          <Table striped bordered hover responsive className="mb-0"> {/* Replaced min-w-full divide-y divide-gray-200 */}
            <thead className="bg-light"> {/* Replaced bg-gray-50 */}
              <tr>
                <th>Store Name</th> {/* Replaced th px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider */}
                <th>Address</th>
                <th>Overall Rating</th>
                <th>Your Rating</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody> {/* Replaced bg-white divide-y divide-gray-200 */}
              {filteredStores.map((store) => (
                <tr key={store.id}> {/* Replaced hover:bg-gray-50 */}
                  <td>{store.name}</td> {/* Replaced td px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 */}
                  <td>{store.address}</td>
                  <td>
                    {typeof store.rating === 'number' && !isNaN(store.rating)
                      ? store.rating.toFixed(2)
                      : (typeof store.rating === 'string' && !isNaN(parseFloat(store.rating))
                          ? parseFloat(store.rating).toFixed(2)
                          : 'N/A')}
                  </td>
                  <td>
                    {userRatings[store.id] || 'Not rated'}
                  </td>
                  <td>
                    <Button
                      variant="info" // Replaced bg-indigo-600
                      onClick={() => openRatingModal(store)}
                      size="sm"
                    >
                      {userRatings[store.id] ? 'Modify Rating' : 'Submit Rating'}
                    </Button>
                  </td>
                </tr>
              ))}
              {filteredStores.length === 0 && (
                <tr>
                  <td colSpan="5" className="text-center text-muted">No stores found.</td> {/* Replaced px-6 py-4 text-center text-gray-500 */}
                </tr>
              )}
            </tbody>
          </Table>
        </div>
      </section>

      {/* Rating Modal */}
      {showRatingModal && selectedStore && (
        <Modal show={true} onHide={() => setShowRatingModal(false)} centered> {/* Replaced fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50 */}
          <Modal.Header closeButton>
            <Modal.Title>Rate {selectedStore.name}</Modal.Title> {/* Replaced h2 text-2xl font-bold text-gray-900 mb-6 text-center */}
          </Modal.Header>
          <Modal.Body className="text-center"> {/* Replaced div mb-4 text-center */}
            <Form.Group className="mb-3">
              <Form.Label className="h5">Your Rating (1-5)</Form.Label> {/* Replaced label block text-lg font-medium text-gray-700 mb-2 */}
              <Form.Control
                type="number"
                min="1"
                max="5"
                value={currentRating}
                onChange={(e) => setCurrentRating(parseInt(e.target.value) || 0)}
                className="w-auto mx-auto text-center" // Replaced w-24 text-center px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500 text-xl
              />
            </Form.Group>
            {ratingMessage && (
              <Alert variant={ratingMessage.startsWith('Error') ? 'danger' : 'success'}> {/* Replaced p tag with Alert */}
                {ratingMessage}
              </Alert>
            )}
          </Modal.Body>
          <Modal.Footer> {/* Replaced div flex justify-end space-x-4 mt-6 */}
            <Button
              variant="secondary" // Replaced bg-gray-300
              onClick={() => setShowRatingModal(false)}
              disabled={loading}
            >
              Cancel
            </Button>
            <Button
              variant="primary" // Replaced bg-indigo-600
              onClick={handleSubmitRating}
              disabled={loading}
            >
              {loading ? 'Submitting...' : 'Submit Rating'}
            </Button>
          </Modal.Footer>
        </Modal>
      )}

      {showUpdatePasswordModal && (
        <UpdatePasswordModal
          onClose={() => setShowUpdatePasswordModal(false)}
        />
      )}
    </Container>
  );
}

export default UserDashboard;
