import React, { useContext, useEffect, useState } from 'react';
import { AuthProvider, AuthContext } from './AuthContext';
import { Container, Alert, Button } from 'react-bootstrap'; // Import Bootstrap components

// Import Page Components (ensure these paths match your actual file names, e.g., Login.js, not LoginPage.js)
import LoginPage from './pages/LoginPage'; // Corrected import path based on previous file structure
import RegisterPage from './pages/RegisterPage'; // Corrected import path based on previous file structure
import AdminDashboard from './pages/AdminDashboard';
import UserDashboard from './pages/UserDashboard';
import StoreOwnerDashboard from './pages/StoreOwnerDashboard';

/**
 * MainAppContent Component:
 * This component conditionally renders the appropriate dashboard or login/register page
 * based on the authentication status and user role.
 * It consumes the AuthContext.
 */
function MainAppContent() {
  const { token, role, logout } = useContext(AuthContext);
  const [isAuthChecking, setIsAuthChecking] = useState(true);
  const [currentPage, setCurrentPage] = useState(window.location.hash === '#register' ? 'register' : 'login');

  // Effect hook to verify token validity on initial component mount
  useEffect(() => {
    const verifyToken = async () => {
      if (token) {
        try {
          const response = await fetch('http://localhost:5000/api/admin/users', {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });

          if (!response.ok) {
            console.warn("Token verification failed, logging out.");
            logout();
          }
        } catch (error) {
            // Log the error for debugging purposes, but don't expose sensitive details to the user
            console.error("Error during token verification:", error);
            // If there's a network error or any other issue, assume the token is invalid or session is broken
            logout();
        }
      }
      setIsAuthChecking(false);
    };

    verifyToken();

    // Optional: Listen for hash changes from browser navigation (e.g., back/forward buttons)
    const handleHashChange = () => {
      setCurrentPage(window.location.hash === '#register' ? 'register' : 'login');
    };
    window.addEventListener('hashchange', handleHashChange);
    return () => {
      window.removeEventListener('hashchange', handleHashChange);
    };
  }, [token, logout]);

  // Display a loading indicator while the authentication status is being verified
  if (isAuthChecking) {
    return (
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <p className="h4 text-muted">Checking authentication...</p>
      </Container>
    );
  }

  let content;
  // Conditional rendering based on whether a valid token exists
  if (!token) {
    // Directly render LoginPage or RegisterPage.
    // The full-screen background and centering will be handled by those components.
    content = (
      <>
        {currentPage === 'login' ? (
          <LoginPage />
        ) : (
          <RegisterPage />
        )}
        {/* The links are now part of LoginPage/RegisterPage themselves,
            so no need to render them here. */}
      </>
    );
  } else {
    // If a token exists, render the appropriate dashboard based on the user's role
    switch (role) {
      case 'admin':
        content = <AdminDashboard />;
        break;
      case 'user':
        content = <UserDashboard />;
        break;
      case 'store_owner':
        content = <StoreOwnerDashboard />;
        break;
      default:
        // Fallback for unrecognized roles or other unauthorized scenarios
        content = (
          <Container className="d-flex justify-content-center align-items-center min-vh-100 flex-column">
            <Alert variant="danger" className="h4 text-center">
              Access Denied: Your role is not recognized or authorized.
            </Alert>
            <Button variant="danger" onClick={logout} className="mt-3">
              Logout
            </Button>
          </Container>
        );
    }
  }

  return (
    // This div provides a base background for the entire app, but individual pages
    // like Login/Register will override it with their own full-screen backgrounds.
    <div className="bg-light font-sans text-dark">
      {content}
    </div>
  );
}

/**
 * App Component:
 * The root component that wraps the MainAppContent with AuthProvider.
 * This ensures that AuthContext is available throughout the application.
 */
function App() {
  return (
    <AuthProvider>
      <MainAppContent />
    </AuthProvider>
  );
}

export default App;
